          <Tr key={i}>
            <Td width="30%">{item.nome}</Td>
            <Td width="30%">{item.email}</Td>
            <Td width="20%" onlyWeb>
              {item.fone}
            </Td>
            <Td alignCenter width="5%">
              <FaEdit />
            </Td>
          </Tr>
        ))}
      </Tbdoy>
    </Table>
  );
};

export default Grid;
